function addBurgerMenuHandler(){let e=document.querySelector(".burger-menu"),t=document.querySelector(".header-menu");e.addEventListener("click",()=>{e.classList.toggle("active"),t.classList.toggle("active"),t.classList.contains("active")?document.body.style.overflow="hidden":document.body.style.overflow=""})}document.addEventListener("DOMContentLoaded",(function(){addBurgerMenuHandler()}));